INSERT INTO `heart_news` VALUES ('39', '127', '0', '1372860791', '0', 'array (\n  \'userid\' => \'1\',\n  \'username\' => \'root\',', 'heartphp_v0.1_2013.07.03 更新日志', '&nbsp;&nbsp;&nbsp;&nbsp;1.整合内容字段文件<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;2.修复过滤GET POST<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;3.增加缓存工具类<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;4.更改目录结构，让目录看起来更人性化<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;5.增加了常量 __D__ __C__ __A__ 来获取URL路由参数<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;6.更改了core::show_error显示的HTML<br />', 'heartphp_v0.1_2013.07.03 更新日志');
